export interface GalleryItem {
  id: number;
  status: number;
  statusCode: string;
  orgId: number;
  orgSlug: string;
  orgName: string;
  slug: string;
  downloads: number;
  revisionId: number;
  revision: number;
  name: string;
  description: string;
  readme: string;
  collectorType: string;
  collectorConfig: any;
  collectorPluginList: string;
  datasource: string;
  privacy: string;
  createdAt: string;
  updatedAt: string;
  isEditor: boolean;
  screenshots: Screenshot[];
  hasLogo: boolean;
  reviewsCount: number;
  reviewsAvgRating: number;
  links: Link[];
  datasourceSlugs: string[];
  logos: {
    small: {
      type: string;
      filename: string;
      content: string;
    };
  };
}

export interface Link {
  rel: string;
  href: string;
}

export interface Screenshot {
  id: number;
  dashboardId: number;
  name: string;
  filename: string;
  mainScreenshot: boolean;
  createdAt: string;
  updatedAt: string;
  links: Link[];
}

export interface GalleryResponse {
  direction: string;
  items: GalleryItem[];
  links: Link[];
  orderBy: string;
  page: number;
  pageSize: number;
  pages: number;
  total: number;
}

interface TemplateGalleryProps {
  items: DashboardDataDTO[];
  onClickItem: (item: DashboardDataDTO) => void;
}

export function TemplateGallery({ items, onClickItem }: TemplateGalleryProps) {
  return items.map((dashboard: DashboardDataDTO) => {
    return (
      <DashboardItem
        key={dashboard.uid}
        dashboard={dashboard}
        onClick={() => {
          onClickItem(dashboard);
        }}
      />
    );
  });
}

function DashboardItem({
  dashboard,
  onClick,
}: {
  dashboard: DashboardDataDTO & { rows?: any[] };
  onClick: () => void;
}) {
  const panelsCount =
    dashboard.panels?.length || dashboard.rows?.reduce((count, r) => (count += r?.panels?.length || 0), 0);
  return (
    <Card onClick={onClick}>
      <Card.Heading>{dashboard.title}</Card.Heading>
      <Card.Description>{dashboard.description}</Card.Description>
      <Card.Tags>
        <TagList tags={dashboard.tags ?? []} />
      </Card.Tags>
    </Card>
  );
}
